import * as process from 'node:process';

import prompts from 'prompts';

import { DEFAULT_CONFIGS_CONFIG } from '@/constants/configs-config';
import { FEATURE_OPTIONS } from '@/constants/features';
import type { ConfigsConfig } from '@/types/configs-config';

import type { InitQuestions } from './types';

interface GetQuestionsOptions {
  currentConfigsConfig?: ConfigsConfig;
}

function getQuestions(options?: GetQuestionsOptions) {
  const configsConfig = options?.currentConfigsConfig ?? DEFAULT_CONFIGS_CONFIG;

  const questions: InitQuestions = [
    {
      type: 'multiselect',
      name: 'featureKeys',
      message: 'select features',
      choices: FEATURE_OPTIONS.map(({ key, displayName }) => ({
        value: key,
        title: displayName,
        selected: Boolean(configsConfig.features?.[key]),
      })),
    },
  ];

  return questions.map((question) => ({
    optionsPerPage: Number.POSITIVE_INFINITY,
    ...question,
  }));
}

interface PromptOptions {
  currentConfigsConfig?: ConfigsConfig;
}

export default async function prompt(options?: PromptOptions) {
  const questions = getQuestions(options);
  return prompts(questions, {
    // eslint-disable-next-line unicorn/no-process-exit, n/no-process-exit
    onCancel: () => process.exit(0),
  });
}

import fs from 'fs-extra';
import { merge } from 'lodash-es';
import * as prettier from 'prettier';

import logger from '@/utils/logger';
import { stringifyJavaScript } from '@/utils/misc';

import type {
  CopySyncOptions,
  MergeJsonFileSyncOptions,
  OutputCjsFileSyncOptions,
  OutputEsmFileSyncOptions,
  OutputFileSyncOptions,
  OutputFormatFileSyncOptions,
  OutputJsonFileSyncOptions,
  ReadFileSyncOptions,
  ReadJsonFileSyncOptions,
} from './types';

function format(source: string) {
  return prettier.format(source, {
    singleQuote: true,
    parser: 'babel',
  });
}

function readFileSync({ filePath }: ReadFileSyncOptions) {
  return fs.readFileSync(filePath, { encoding: 'utf8' });
}

async function outputFile({
  filePath,
  data,
  isTrim = true,
  isInsertNewLine = true,
  isFormat = false,
}: OutputFileSyncOptions) {
  let content = isTrim ? data.trim() : data;
  content = isInsertNewLine ? `${content}\n` : content;
  content = isFormat ? await format(content) : content;
  fs.outputFileSync(filePath, content, { encoding: 'utf8' });
  logger.info(`output ${filePath}`);
}

function readJsonFileSync({ filePath }: ReadJsonFileSyncOptions): unknown {
  return fs.readJsonSync(filePath, {
    encoding: 'utf8',
    throws: false,
  });
}

function outputJsonFileSync({ filePath, data }: OutputJsonFileSyncOptions) {
  fs.outputJsonSync(filePath, data, {
    encoding: 'utf8',
    spaces: 2,
    finalEOL: true,
  });
  logger.info(`output ${filePath}`);
}

function mergeJsonFileSync({ filePath, data }: MergeJsonFileSyncOptions) {
  const prevData = readJsonFileSync({ filePath });
  const finalData = merge({}, prevData, data);
  outputJsonFileSync({ filePath, data: finalData });
}

async function outputEsmFile({
  filePath,
  data,
  banner = '',
  isFormat = true,
}: OutputEsmFileSyncOptions) {
  const content = `${banner}
  export default ${stringifyJavaScript(data) ?? ''};`;
  await outputFile({
    filePath,
    data: content,
    isFormat,
  });
}

async function outputCjsFile({
  filePath,
  data,
  banner = '',
  isFormat = true,
}: OutputCjsFileSyncOptions) {
  const content = `${banner}
  module.exports = ${stringifyJavaScript(data) ?? ''};`;
  await outputFile({
    filePath,
    data: content,
    isFormat,
  });
}

async function outputFormatFile({
  filePath,
  format: formatType,
  data,
  banner = '',
}: OutputFormatFileSyncOptions) {
  switch (formatType) {
    case 'json': {
      outputJsonFileSync({ filePath, data });
      break;
    }
    case 'esm': {
      await outputEsmFile({ filePath, data, banner });
      break;
    }
    case 'cjs': {
      await outputCjsFile({
        filePath,
        data,
        banner,
      });
      break;
    }
    case 'text': {
      await outputFile({ filePath, data });
      break;
    }
    default: {
      await outputFile({ filePath, data });
      break;
    }
  }
}

function copySync({ src, dest, ...rest }: CopySyncOptions) {
  fs.copySync(src, dest, rest);
  logger.info(`copy ${src} to ${dest}`);
}

export {
  copySync,
  mergeJsonFileSync,
  outputFile,
  outputFormatFile,
  readFileSync,
  readJsonFileSync,
};

import fs from 'fs-extra';
import latestVersion from 'latest-version';
import ora from 'ora';
import shell from 'shelljs';
import type { PackageJson } from 'type-fest';

import * as files from '@/utils/files';
import logger from '@/utils/logger';
import * as paths from '@/utils/paths';

import type { MergePackageJsonSyncOptions } from './types';

export function readRootPackageJsonSync() {
  const filePath = paths.resolveRoot('package.json');
  return files.readJsonFileSync({ filePath }) as PackageJson;
}

function existsCwdPackageJsonSync() {
  return fs.existsSync(paths.cwdPackageJson);
}

export function checkCwdPackageJsonSync() {
  const isExistsCwdPackageJson = existsCwdPackageJsonSync();

  if (isExistsCwdPackageJson) {
    return true;
  }

  logger.error(`${paths.cwdPackageJson} does not exist, please run "npm init"`);
  return false;
}

export function mergeCwdPackageJsonSync({ data }: MergePackageJsonSyncOptions) {
  const filePath = paths.cwdPackageJson;
  files.mergeJsonFileSync({ filePath, data });
}

export function sortCwdPackageJsonSync() {
  shell.exec(`sort-package-json --quiet "${paths.cwdPackageJson}"`);
  logger.info(`sort ${paths.cwdPackageJson}`);
}

export async function fetchPackageLatestVersion(packageName: string) {
  const spinner = ora({
    prefixText: '[Package Latest Version]',
  });
  try {
    spinner.start(`${packageName} ...`);
    const version = await latestVersion(packageName);
    spinner.succeed(`${packageName}@${version}`);
    return version;
  } catch (error) {
    spinner.fail(`${packageName}: ${(error as Error).message}`);
    return '';
  }
}
